   const deleteBlog=(id)=>{
        const newBlog = blogs.filter(blog => blog.id !== id)
        setblogs(newBlog)
   }

   useEffect(()=>{
       fetch('http://localhost:8000/blogs').
       then((res) => {return res.json()}).then(data => {setblogs(data)})

    },[])

    return ( 
        <div className="home">


            {isPending && <div>is loauding .......</div>}
