const useFetch=(url) =>{
    const [Data, setData] = useState(null )
    const [isPending, setisPending] = useState(true)


    useEffect(()=>{
        fetch(url).
        then((res) => {return res.json()}).then(data => {setblogs(data)})
        setisPending(false)
     },[url])

     return {Data, isPending}



}

export default useFetch;
